:root{
    --colorPrincipal:#328cc1;
    --colorSecundario:#0b3c5d;
    --colorOscuro:#1d2731;
    --resaltar:#d9b310;
  }
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
  html{
    font-size: 10px;
  }
  body{
    background-color: #F5F6F0;
    color: #020F14;
    font-family: 'Open Sans', sans-serif;
    text-align: center;
    font-size: 1.4rem;
    height: 100vh;
  }
  h1{
    font-size: 2.6rem;
  }
  h2{
    font-size: 2.4rem;
  }
  .piePagina {
    left: 0;
    bottom: 0;
    width: 100%;
    background-color: var(--colorOscuro);
    color: white;
    text-align: center;
    padding: 10px 10px 3px 10px;
    font-size: 1.1rem;
    clip-path: circle(55px at 50% 100%);
    transition: all 0.6s;
  }
  .piePagina-info{
    padding-top: 11px;  
  }

  /* PAGINA DE INICIO */
  .seccionCabecera{  
    background-color: var(--colorSecundario);
  }
  .cabecera-container{
    width: 100%;
    padding: 10px;
  }
  .cabecera-container li{
    list-style: none;
  }
  .cabecera-container a{
    text-decoration: none;
    color: whitesmoke;
  }
  .cabecera-container a:hover{
    text-decoration: underline;
    color: var(--resaltar);
  }
  .contenedorRedesSociales{
    padding-top: 15px;
  }
  .contenedorRedesSociales li{
    display: inline-block;
    padding-right: 6px;
  }
  .contenedorRedesSociales img{
    width: 24px;
    transition: transform .2s;
  }
  .cabecera-logo{
    font-size: 2.6rem;
    padding: 8px;
  }
  .cabecera-itemsLogeo{
    font-size: 1.3rem;
  }
  .cabecera-itemsNavegacion li{
    text-transform: uppercase;
    font-weight: bold;
    line-height: 20px;
  }
  .inicioSeccion{
    padding-top: 20px;
    padding-bottom: 20px;
    width: 75%;
    margin: auto;
  }

  /* formulario regitro */
  .formulario{
    width: 100%;
    padding-top: 20px;
  }
  .formulario-inputs input{
    width: 100%;
    display: flex;
    flex-direction: column;
    height: 30px;
    margin-bottom: 12px;
  }
  .seccionPolitica{
    font-family: 'Times New Roman', Times, serif;
    font-size: 1.2rem;
  }
  .seccionPolitica-aceptar{
    padding-top: 15px;
    padding-bottom: 15px;
  }
  .btn{
    color: #FFFFFF;
    background-color: var(--colorSecundario);
    border-radius: 3px;
    font-size: 12px;
    padding: 10px 20px;
    border: none;
    cursor: pointer;
  }
  /* CARRUSEL */
.carrusel{
    width: 100%;
  }
  /* imagenes */
  .tarjeta-list{
    position: relative;
    display: flex;
    align-items: center;
    height: 304px;
    padding: 10px 0px;
    margin: 0px auto;
    max-width: 90vw;
    overflow: hidden;
  }
  .tarjeta-track{
    position: relative;
    top: 0;
    left: 0;
    display: flex;
    justify-content: center;
    transition: .5s ease-in-out;
  }
  .tarjeta{
    position: relative;
    width: 275px;
    padding: 0 18px;
    float: left;
    box-sizing: border-box;
    display: flex;
    height: 100%;
  }
  .tarjeta a img{
    object-fit: cover;
    width: 100%;
    border-radius: 4px;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,.5);
    transition: .3s ease-in-out;
  }
  /* botones */
.tarjeta-arrow{
    border-radius: 30px;
    background-color: #fff;
    position: absolute;
    z-index: 4;
    width: 48px;
    height: 48px;
    text-align: center;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,.15);
    border: 0;
    cursor: pointer;
    font-size: 30px;
  }
  .tarjeta-prev{
    left: 0px;
  }
  .tarjeta-next{
    right: 0px;
  }
  /* FIN CARRUSEL */

  .bannerEmpresa{
    background-color: var(--colorPrincipal);
    color: white;
    box-shadow: 0px 8px 20px -4px rgba(17,19,26,1); 
  }
  .bannerEmpresa-bloque{
    padding: 15px;
    border-top: dotted white 1px;
  }
  .centrarLista{
    text-align: center;
    list-style-position: inside;
    padding-top: 5px;
    padding-bottom: 5px;
  }
  .justificarLista{
    text-align: justify;
    list-style-position: inside;
    padding-top: 5px;
    padding-bottom: 5px;
  }
  .piePagina-itemsNavegacion li{
    text-transform: uppercase;
    font-weight: bold;
    line-height: 20px;
  }
  .piePagina-itemsNavegacion a{
    text-decoration: none;
    color: white;
  }
  /* minimo tablet */
@media (min-width: 768px){
    .cabecera-container{
        display: flex;
        justify-content: space-between;
        align-items: center;
    }
    .cabecera-container div:first-child{
        order:3;
    }
    .cabecera-itemsNavegacion li{
      display: inline-block;
    }
    .cabecera-itemsLogeo li{
      display: inline-block;
    }
    .cabecera-itemsNavegacion li:not(:first-child)::before{
      content: "|";
      color: whitesmoke;
    }
    .cabecera-itemsLogeo li:not(:first-child)::before{
      content: "|";
      color: whitesmoke;
    }
    .bannerEmpresa-franja{
      display: grid;
      grid-template-columns: 1fr 1fr;
    }
    .bannerEmpresa-bloque{    
      border: none;
      border-right: dotted white 1px;  
    }
    .contenedorLibros{
      display: grid;
      column-gap: 25px;
      grid-template-columns: 1fr 1fr;
    }
    .contenidoRegistro{
      display: grid;
      grid-template-columns: 1fr 2fr;
      align-content: center;   
    }
    .piePagina{
      display: flex;
      justify-content: space-between;
      align-items: center;    
    }
    .piePagina-itemsNavegacion li{
      display: inline-block;
    }
    .piePagina-itemsNavegacion li:not(:first-child)::before{
      content: "|";
    }
    .piePagina :nth-child(1) { 
      order:1;
    }
    .piePagina :nth-child(2) { 
      order:3;
    }
    .piePagina :nth-child(3) { 
      order:2;   
    }
    .piePagina-info{
      margin-left: -240px;
    }

    /* detalle lectura */
    .contenedorLectura{
      width: 95%;
      display: grid;
      column-gap: 15px;
      grid-template-columns: 1fr 2fr 0.7fr;
      align-content: center;
    }
  }
  /* large screen */
  @media (min-width: 900px){
    .contenedorLibros{
      display: grid;
      column-gap: 25px;
      grid-template-columns: 1fr 1fr 1fr;
    }
  }
